const renderEntry = (entry) => {
  const a =  `
    <dl class="entry">
      <dt>
        <a href="?q=${entry.root}" class="wordbox"><b>${entry.root.normalize("NFD")}</b></a><div class="refers">${entry.refers??""}</div>${
          entry.stems?`
      </dt><dd><dl>
      ${entry.stems.flatMap((item,stemno) => `<dt>Stem ${stemno+1}:</dt><dd>`+(typeof item === "object" ? (`<dl>`+["BSC","CTE","CSV","OBJ"].map(x=>`<dt>${x}</dt><dd>${item[x].replace(/\*\*([^*]+)\*\*/g, "<b>$1</b>").replace(/\*([^*]+)\*/g, "<i>$1</i>")}</dd>`).join("")+`</dl>`): item.replace(/\*\*([^*]+)\*\*/g, "<b>$1</b>").replace(/\*([^*]+)\*/g, "<i>$1</i>"))+`</dd>`).join("")}</span></li>
      </dl></dd>`:""}
      ${entry.see?`&emsp; see <a href="?q=${entry.see}&lang=ith4"><b>${entry.see}</b></a>`:""}
    </dl>`;
    return a
}

