const rankEntries= (data, qRaw) =>  {
  const query = qRaw.toLowerCase().normalize("NFD");
  let a =  data
    .map(entry => {
    try{
      const word = entry.root.toLowerCase().normalize("NFD");
      const wordascii = word.replace("q","").replace(/t[sdt]/,"t");
      const def = entry.stems?entry.stems.flatMap(item => typeof item === "object" ? Object.values(item): item):[];
      const name = entry.refers??""
      const nameword = name.split("")
      
      let s = 0;
      if ((word === query)) s += 8;
      else if ((wordascii === query)) s += 7;
      else if (word.startsWith(query)) s += 5
      else if (wordascii.startsWith(query)) s += 4;
      if (nameword.includes(query)) s += 4;
      else if (name.startsWith(query)) s += 3;
      else if (name.includes(query)) s += 2;
      if (def.some(x=>x.split(" ").includes(query))) s += 4;
      else if (def.some(x=>x.includes(query))) s += 3;
      else if (s===0) return null;
      return {entry, rank: s};}
      catch (error){
      console.log(error, entry)}
    }).filter(Boolean).sort((a, b) => b.rank - a.rank).map(x => x.entry);
    return a
}

export {DICT, desc, renderEntry, numbermaker,rankEntries};